        yOffset = yOffset + 45;
    if(entity.column > 3)
        yOffset = yOffset + 50;
    if(entity.column > 5)
        yOffset = yOffset + 50;
    if(entity.column > 7)
        yOffset = yOffset + 50;
    if(entity.column > 9)
        yOffset = yOffset + 50;

    var xPos = xOffset;
    var yPos = yOffset -70;
    var entityId = entity.id;

   /* //generem un color pel jugador aleatoriament
    var colour= '#';

    if(entity.owner != null){
        var hash = 0;
        for (var i = 0; i < entity.owner!.length; i++) {
            hash = entity.owner!.charCodeAt(i) + ((hash << 5) - hash);
        }
        for (var i = 0; i < 3; i++) {
             var value = (hash >> (i * 8)) & 0xFF;
            colour += ('00' + value.toString(16)).substr(-2);
        }
    }else{
        colour = "white"
    }
    */

  return (
      <g id="road">
        <Road id={entityId} cx={xPos} cy={yPos} width="80" height="20" stroke="black" stroke-width="3" fill="white"></Road>
      </g>    
  );
}
