import { Middleware } from "redux";
import { AppState } from "www/store";
import { gamePost } from "www/store/game/gamePost";
import { ComponentTradeActionTypes, TRADE } from "./types";

export const componentTradeMiddleware: Middleware<{}, AppState> =
  (store: any) => (next) => async (action: ComponentTradeActionTypes) => {
    next(action);
    console.log("reach middleware + action: "+action.type);
    if (action.type === TRADE) {
      await gamePost(
        store,
        `/api/v1/resources/${action.entityId}/${action.givenResources}/${action.getResources}/${action.playerName}/trade`,
        {}
      );
    }
  };
