import styled from "styled-components";


const Hex = styled.polygon`
cursor: pointer;
fill-opacity: 0.4;
stroke: #000;
stroke-width: 1;
transition: transform, fill-opacity, stroke-width;
-webkit-transition: transform, fill-opacity, stroke-width;
transition-duration: 1s;
-webkit-transition-duration: 1s;;
transform-origin: 50% 50%;
transform: rotate(30deg);
&:hover{
  fill-opacity: 1;
  stroke: #ff0000;
  stroke-width: 5;
}
`


export function HexComponent({ entity }: any) {
  var SEA = -1;
  var DESSERT = -2;
  var LUMBER = 0;
  var GRAIN = 1;
  var WOOL = 2;
  var BRICK = 3;
  var ORE = 4;

  var color = "";
  if(entity.type === "tile"){
      switch(entity.TileType){
        case DESSERT:
          color="#663300";
          break;
        case SEA:
          color="#663300";
          break;
        case LUMBER:
          color="#663300";
          break;
        case GRAIN:
          color="#663300";
          break;
        case WOOL:
          color="#663300";
          break;
        case BRICK:
          color="#663300";
          break;
        case ORE:
          color="#663300";
          break;
        default:
          color="#000000";
      }
      console.log(entity);
  };
  return (
      <g id="hex">
        <Hex  fill={color} points="150,75 112,140 37,140 0,75 37,10 112,10"></Hex>
        <text x="0" y="0" text-anchor="middle"> {entity.TileWeight}</text>
      </g>
  );
}
