import { useAppSelector , useInputRef, useDispatchFormBig} from "www/store/hooks";
import { getPlayerName } from "www/store/player/selectors";
import { trade } from "./actions";


export function TradeComponent({ entity }: any) {
  const currentPlayer = useAppSelector(getPlayerName);
  const givenResourcesRef = useInputRef();
  const getResourcesRef = useInputRef();



  const trading = useDispatchFormBig(
    trade,
    entity.id,
    givenResourcesRef,
    getResourcesRef,
    currentPlayer
  );


  if(entity.owner !== currentPlayer || entity.type !== 'inventory') return null;

  return <>

      <label>
        Give Resource of type:
        <input type="text" ref={givenResourcesRef} />
      </label>
      <label>
        Get Resource of type:
        <input type="text" ref={getResourcesRef} />
      </label>
      <button onClick={trading}>
        Trade
      </button>

        <div> entityId: {entity.id} </div>
        </>
}
