import { SSL_OP_SSLEAY_080_CLIENT_DH_BUG } from "constants";
import styled from "styled-components";


const Hex = styled.polygon`
cursor: pointer;
fill-opacity: 0.4;
stroke: #000;
stroke-width: 1;
transition: transform, fill-opacity, stroke-width;
-webkit-transition: transform, fill-opacity, stroke-width;
transition-duration: 1s;
-webkit-transition-duration: 1s;;
transform-origin: 50% 50%;
transform: rotate(30deg);
&:hover{
  fill-opacity: 1;
  stroke: #ff0000;
  stroke-width: 5;
}
`


export function HexComponent({ entity }: any) {
  var SEA = -1;
  var DESSERT = -2;
  var LUMBER = 0;
  var GRAIN = 1;
  var WOOL = 2;
  var BRICK = 3;
  var ORE = 4;

  var mapSize = 7;

  var locations = new Array(7).fill(new Array(7).fill('#'));
  //var rowLocations = ["", "", "", "", "", "", ""];
  var x = [0,37,112,150,112,37];
  var y = [75,10,10,75,140,140];

  for(var i = 0; i < mapSize; i++){
    //locations.push(rowLocations);
    for(var j = 0; j < mapSize; i++){
      var auxString = `${x[0]},${y[0]} ${x[1]},${y[0]} ${x[2]},${y[0]} ${x[3]},${y[0]} ${x[4]},${y[0]} ${x[5]},${y[0]}`;
        locations[0][0] = auxString;
        for(var k = 0; k < x.length; k++){
          var auxX = x[k] + 30;
          x[k] = auxX;
        }
    }
    for(var l = 0; l < y.length; l++){
      var auxY = y[l] + 30;
      y[l] = auxY;
    }
  }

  //var locations = [[0,1,2,"0,75 37,10 112,10 150,75 112,140 37,140",  "30,75 67,10 142,10 180,75 142,140 67,140",   "60,75 97,10 172,10 210,75 172,140 97,140",   "0,75 37,10 112,10 150,75 112,140 37,140"pos7]]

  var color = "";
  if(entity.type === "tile"){
      switch(entity.TileType){
        case DESSERT:
          color="#663300";
          break;
        case SEA:
          color="#663300";
          break;
        case LUMBER:
          color="#663300";
          break;
        case GRAIN:
          color="#663300";
          break;
        case WOOL:
          color="#663300";
          break;
        case BRICK:
          color="#663300";
          break;
        case ORE:
          color="#663300";
          break;
        default:
          color="#000000";
      }
      console.log(entity);
  };
  return (
      <g id="hex">
        <Hex  fill={color} points={locations[entity.row][entity.column]}></Hex>
        <text x="700" y="700" text-anchor="middle"> {entity.TileWeight}</text>
      </g>
  );
}
