import styled from "styled-components";
import { useAppSelector, useDispatchFormBig } from "www/store/hooks";
import { getPlayerName } from "www/store/player/selectors";
import { own } from "./OwnComponent/actions";

const Vertex = styled.circle`
cursor: pointer;
fill-opacity: 0.4;
stroke: #000;
stroke-width: 1;
transition: transform, fill-opacity, stroke-width;
-webkit-transition: transform, fill-opacity, stroke-width;
transition-duration: 1s;
-webkit-transition-duration: 1s;;
&:hover{
  fill-opacity: 1;
  stroke: #ff0000;
  stroke-width: 5;
}
`
//transform-origin: 50% 50%;

//transform: rotate(30deg);



export function VertexComponent({ entity }: any) { 
  const currentPlayer = useAppSelector(getPlayerName);
  const owned = useDispatchFormBig(own, entity.id, currentPlayer);

  if(entity.type !== "vertex") return null;

    var yOffset = entity.column*32;
    var xOffset = entity.row*65 +66;


    const changeStyle = () => {
        console.log(entity.id);
      };

    if(entity.column > 1)
        yOffset = yOffset + 45;
    if(entity.column > 3)
        yOffset = yOffset + 50;
    if(entity.column > 5)
        yOffset = yOffset + 50;
    if(entity.column > 7)
        yOffset = yOffset + 50;
    if(entity.column > 9)
        yOffset = yOffset + 50;

    var xPos = xOffset;
    var yPos = yOffset -70;
    var entityId = entity.id;

  return (
      <g id="vertex">
        <Vertex id={entityId} cx={xPos} cy={yPos} r="20" stroke="black" stroke-width="3" fill="white"  onClick={() => {
            owned(); 
  }}></Vertex>
      </g>    
  );
}
