import styled from "styled-components";


const Hex = styled.polygon`
cursor: pointer;
fill-opacity: 0.4;
stroke: #000;
stroke-width: 1;
transition: transform, fill-opacity, stroke-width;
-webkit-transition: transform, fill-opacity, stroke-width;
transition-duration: 1s;
-webkit-transition-duration: 1s;;
transform-origin: 50% 50%;
transform: rotate(30deg);
&:hover{
  fill-opacity: 1;
  stroke: #ff0000;
  stroke-width: 5;
}
`


export function HexComponent({ entity }: any) {
  var SEA = -1;
  var DESSERT = -2;
  var LUMBER = 0;
  var GRAIN = 1;
  var WOOL = 2;
  var BRICK = 3;
  var ORE = 4;
  if(entity.type === "tile"){
      switch(entity.TileType){
        case DESSERT:
          break;
        case SEA:
          break;
        case LUMBER:
          break;
        case GRAIN:
          break;
        case WOOL:
          break;
        case BRICK:
          break;
        case ORE:
          break;
      }
      console.log(entity);
  };
  var test = "#ffff00"
  return (
      <g id="hex">
        <Hex fill={test} points="150,75 112,140 37,140 0,75 37,10 112,10"></Hex>
      </g>
  );
}
